import React from "react";
import Navbar from "../components/Navbar";
import { InputNumber } from "antd";
import CartItem from "../components/CartItem";

const onChange = (value) => {
    console.log("changed", value);
};

function Cart() {
    return (
        <div>
            <Navbar />
            <div style={{ padding: "5%" }}>
                {" "}
                <h2
                    style={{
                        backgroundColor: "#d3d3d3",
                        width: "100%",
                        height: "70px",
                        padding: "11px",
                        textAlign: "center",
                    }}
                >
                    Giỏ hàng của bạn
                </h2>
                <div style={{}}>
                    <table
                        style={{ width: "100%", borderCollapse: "collapse" }}
                    >
                        <thead>
                            <tr>
                                <th style={tableHeaderStyle}></th>
                                <th style={tableHeaderStyle}>Tên sản phẩm</th>
                                <th style={tableHeaderStyle}>Giá</th>
                                <th style={tableHeaderStyle}>Số lượng</th>
                                <th style={tableHeaderStyle}>Thành tiền</th>
                                <th style={tableHeaderStyle}></th>
                            </tr>
                        </thead>
                        <tbody>
                            <CartItem />
                        </tbody>
                    </table>
                </div>
                <div style={{ marginTop: "3%" }} className="row">
                    <div className="col-7">
                        <h3>Ghi chú đơn hàng</h3>
                    </div>
                    <div className="col-5">
                        <h3>Thông tin đơn hàng</h3>
                        <p style={{ display: "flex" }}>
                            Tổng tiền:
                            <b>
                                <h5>4,200,000₫</h5>
                            </b>
                        </p>
                        <p>
                            Phí vận chuyển sẽ được tính ở trang thanh toán.{" "}
                            <br />
                            Bạn cũng có thể nhập mã giảm giá ở trang thanh toán.
                        </p>
                        <button
                            style={{
                                width: "150px",
                                height: "40px",
                                backgroundColor: "#1e3d37",
                                color: "white",
                            }}
                        >
                            Thanh toán
                        </button>
                    </div>
                </div>
            </div>
        </div>
    );
}

const tableHeaderStyle = {
    backgroundColor: "#d3d3d3",
    padding: "10px",
    textAlign: "center",
    borderBottom: "1px solid #ccc",
};

const tableCellStyle = {
    padding: "10px",
    borderBottom: "1px solid #ccc",
    textAlign: "center",
};

export default Cart;
