import React, { useState } from "react";
import { InputNumber } from "antd";
const onChange = (value) => {
    console.log("changed", value);
};
function CartItem() {
    const [quantity, setQuantity] = useState(0);
    return (
        <div className="cart-item">
            <div style={{}}>
                <table style={{ width: "100%", borderCollapse: "collapse" }}>
                    <thead>
                        <tr>
                            <th style={tableHeaderStyle}></th>
                            <th style={tableHeaderStyle}>Tên sản phẩm</th>
                            <th style={tableHeaderStyle}>Giá</th>
                            <th style={tableHeaderStyle}>Số lượng</th>
                            <th style={tableHeaderStyle}>Thành tiền</th>
                            <th style={tableHeaderStyle}></th>
                        </tr>
                    </thead>
                    <tbody>
                        <tr>
                            <td style={tableCellStyle}>
                                <img
                                    style={{
                                        width: "100px",
                                    }}
                                    src="https://product.hstatic.net/200000404397/product/tinh_che_1_c8d7ce529c244aa3ab855e9a34c8a990_medium.jpg"
                                    alt=""
                                />
                            </td>
                            <td style={tableCellStyle}>
                                <span>Tổ Yến Tinh Chế VIP Loại 1</span> <br />
                                <span>100gram</span>
                            </td>
                            <td style={tableCellStyle}>4,200,000₫</td>
                            <td style={tableCellStyle}>
                                <div className="quantity-area d-flex mt-2">
                                    <input
                                        type="button"
                                        value="-"
                                        onClick={() => {
                                            if (quantity > 0)
                                                setQuantity(quantity - 1);
                                        }}
                                    />
                                    <input type="text" value={quantity} />
                                    <input
                                        type="button"
                                        value="+"
                                        onClick={() =>
                                            setQuantity(quantity + 1)
                                        }
                                    />
                                </div>
                            </td>
                            <td style={tableCellStyle}>4,200,000₫</td>
                            <td style={tableCellStyle}>
                                <i class="fa-solid fa-trash"></i>
                            </td>
                        </tr>
                    </tbody>
                </table>
            </div>
        </div>
    );
}
const tableHeaderStyle = {
    backgroundColor: "#d3d3d3",
    padding: "10px",
    textAlign: "center",
    borderBottom: "1px solid #ccc",
};

const tableCellStyle = {
    padding: "10px",
    borderBottom: "1px solid #ccc",
    textAlign: "center",
};
export default CartItem;
