import { legacy_createStore as createStore } from "redux";

let initialState = {
    cart: [],
};
const store = createStore((state = initialState, action) => {
    if (action.type === "ADD_TO_CART") {
        let cart = [...state.cart];
        let { payload } = action;
        let findIndex = cart;
    }
    return state;
});

export default store;
