import React, { useEffect, useState } from "react";
import Navbar from "../components/Navbar";
import BreadCrumb from "../components/BreadCrumb";
import ProductCarousel from "../components/ProductCarousel";
import { useParams } from "react-router-dom";
function Detail() {
    let [product, setProduct] = useState({});
    let id = useParams();
    console.log(id);
    useEffect(async () => {
        try {
            fetch("http://localhost:3001/api/v1/products/" + id);
            let data = await response.json();
            setProduct(() => {
                return { ...data };
            });
        } catch (error) {
            console.log(error);
        }
    }, []);
    console.log(product);
    return (
        <div className="DetailPage">
            <Navbar />
            <BreadCrumb />
            <section className=" container product-detail">
                <div className="row">
                    <h3>{product.name}</h3>
                    <i className="product-sku">SKU: {product.product_id}</i>
                    <p className="mini-description">
                        <span className=" mini-description-item product-origin">
                            <b>Thương Hiệu</b>: {product.name}
                        </span>
                        <span className=" mini-description-item product-status">
                            <b>Tình trạng</b>:{" "}
                            {product.number === 0 ? "Hết hàng" : "Còn hàng"}
                        </span>
                        <span className=" mini-description-item product-sell">
                            <b>Đã bán</b>: 21 sản phẩm
                        </span>
                    </p>
                </div>
                <div className="row">
                    <div className="col-5">
                        <ProductCarousel />
                    </div>
                    <div className="col-4"></div>
                    <div className="col-3"></div>
                </div>
            </section>
        </div>
    );
}

export default Detail;
