import React, { useState } from "react";
import Navbar from "../components/Navbar";
import BreadCrumb from "../components/BreadCrumb";
import ProductCarousel from "../components/ProductCarousel";
import {useParam} from "react-router-dom"
function Detail() {
    let [product, setProduct] = useState({});
    let id = useParam();
    return (
        <div className="DetailPage">
            <Navbar />
            <BreadCrumb />
            <section className=" container product-detail">
                <div className="row">
                    <h3>To yen tinh che vip 1</h3>
                    <i className="product-sku">SKU: 1231213</i>
                    <p className="mini-description">
                        <span className=" mini-description-item product-origin">
                            Cici yen thuong dinh
                        </span>
                        <span className=" mini-description-item product-status">
                            Con hang
                        </span>
                        <span className=" mini-description-item product-sell">
                            Da ban: 21 san pham
                        </span>
                    </p>
                </div>
                <div className="row">
                    <div className="col-5">
                        <ProductCarousel />
                    </div>
                    <div className="col-4"></div>
                    <div className="col-3"></div>
                </div>
            </section>
        </div>
    );
}

export default Detail;
